mapper.update:

update RELATIONSHIP
set HOST_USER_ID = #{hostUser.userId,jdbcType=INTEGER},
  FRIEND_USER_ID = #{friendUser.userId,jdbcType=INTEGER},
  REMARK = #{remark,jdbcType=VARCHAR},
  ADD_TIME = #{addTime,jdbcType=TIMESTAMP}
where RELATIONSHIP_ID = #{relationshipId,jdbcType=INTEGER}

mapper.select:

select RELATIONSHIP_ID, HOST_USER_ID, FRIEND_USER_ID, REMARK, ADD_TIME
from RELATIONSHIP
where RELATIONSHIP_ID = #{relationshipId,jdbcType=INTEGER}
